mapper.select:
select *
from scm
where scm.scmId = #{scmId}

		select
   		*
    from
         book
    where
         book.subjectId = #{subjectId }
         and book.bookId in (
         	select bookId from scm_book
             	where scm_book.scmId in (
             		select school_scm.scmId from school_scm where school_scm.schoolId = #{schoolId}
             	)
         )

