mapper.select:
select *
from school
where school.schoolId = #{schoolId}

select * from school where true
and school.regionId = #{regionId}
and school.category = #{categoryId }

        select
            *
        from
            school
        where 
        	true
        	
                and category = #{category }
            
            
				and category != 6
            
            
                and provinceId = #{provinceId }
            
            
                and cityId = #{cityId }

select *
from region
where region.regionId = #{regionId}

select *
from city
where city.cityId = #{cityId}

select *
from city
where city.provinceId = #{provinceId}

select * from cls where schoolId = #{schoolId }
and clsId != #{clsId}
order by clsId ASC

