mapper.update:
update
	visit_count
set
	count = #{count}
where
	day = #{day}

	update admin
    set isDeleted = 1
    where adminId = #{adminId}

	update admin
    set isDeleted = 0
    where adminId = #{adminId}

mapper.select:
select count(*) from notification where true
and firstMenuId = #{firstMenuId}
and secondMenuId = #{secondMenuId}
order by createTime desc

select notification.*, second_menu.secondMenuName from notification join second_menu using(secondMenuId) where true
and notification.firstMenuId = #{firstMenuId}
and notification.secondMenuId = #{secondMenuId}
order by createTime desc limit #{limitBegin}, #{pageSize}

select count(*) from notification where isDeleted = 0
and firstMenuId = #{firstMenuId}
and secondMenuId = #{secondMenuId}
order by createTime desc

select notification.*, second_menu.secondMenuName from notification join second_menu using(secondMenuId) where notification.isDeleted = 0
and notification.firstMenuId = #{firstMenuId}
and notification.secondMenuId = #{secondMenuId}
order by createTime desc limit #{limitBegin}, #{pageSize}

select notification.*, second_menu.secondMenuName
from notification join second_menu using (secondMenuId)
where 
	title like #{searchText}
limit #{limitBegin}, #{pageSize}

mapper.update:
update
	image
set
	notificationId = #{notificationId}
where
	imageId = #{imageId}

mapper.select:
select * from notification where creatorId = #{userId} and isDeleted = 0
and createTime <= #{createTime}
order by createTime desc limit #{limitBegin}, #{pageSize}

select * from notification join user_notification using (notificationId) where user_notification.userId = #{userId} and notification.isDeleted = 0
and notification.createTime <= #{createTime}
order by notification.createTime desc limit #{limitBegin}, #{pageSize}
